mapper.select:
SELECT exercise_id AS exerciseId, user_id AS userId, facility_id AS facilityId, exercise_type AS exerciseType, duration, calories_burned AS caloriesBurned FROM exercise WHERE user_id = #{userId}
AND DATE(created_at) = TO_DATE(#{createdAt}, 'YYYY-MM-DD')
ORDER BY created_at DESC

SELECT diet_id AS dietId, user_id AS userId, meal_type, calories, protein, carbs FROM diet WHERE user_id = #{userId}
AND DATE(created_at) = TO_DATE(#{createdAt}, 'YYYY-MM-DD')
ORDER BY created_at DESC

SELECT sleep_id AS sleepId, user_id AS userId, sleep_start AS sleepStart, sleep_end AS sleepEnd, sleep_quality AS sleepQuality FROM sleep WHERE user_id = #{userId}
AND DATE(created_at) = TO_DATE(#{createdAt}, 'YYYY-MM-DD')
ORDER BY created_at DESC

SELECT metric_id AS metricId, user_id AS userId, weight, target_weight AS targetWeight, bmi FROM health_metrics WHERE user_id = #{userId}
AND DATE(created_at) = TO_DATE(#{createdAt}, 'YYYY-MM-DD')
ORDER BY created_at DESC
